tempo = (60 / song[1]) / 4

#Plays song
def playSong(song):
    for i in range(3, len(song)):
        if (song[i] == " "):
            stopSound()
        else:
            playSound(notes[song[i]], song[2])
        sleep(tempo)
    stopSound()

