#Plays sounds
def separateNotes(sound):
    notes = []
    newSound = sound
    
    while not len(newSound) <= 3:
        #Separates second note from third if third exists
        if search("S", newSound) and newSound.index("S") == 1:
            notes.append(newSound[0:3])
            newSound = newSound[3::]
        else:
            notes.append(newSound[0:2])
            newSound = newSound[2::]
    notes.append(newSound)
    notes.append(" ")
    return notes

def playSounds(sounds):
    index = 0
    while sounds[index] != " " and sounds[index] != "" and index < 24:
        buzzers[index].duty_u16(defaultLoudness)
        buzzers[index].freq(notes[sounds[index]])
        index += 1

#Stops sounds
def stopSound():
    for buzzer in buzzers:
        buzzer.duty_u16(0)


#LCD
i2c = I2C(0, sda=Pin(0), scl=Pin(1), freq=400000)
I2C_ADDR = i2c.scan()[0]
lcd = I2cLcd(i2c, I2C_ADDR, 2, 16)

#Displays song name
def showSongName(name):
    print(f"Now playing: {name}")
    
    #Wraps text (makes sure words aren't cut)
    if len(name) <= 16:
        lcd.putstr(name)
    else:
        words = name.split(" ")
        length = len(words[0])
        lcd.putstr(words[0])
        
        for x in range(1, len(words)):
            if length + len(words[x]) + 1 <= 16:
                lcd.putstr(f" {words[x]}")
                length += len(words[x]) + 1
            else:
                lcd.putstr(f"\n{words[x]}")
                length = 0


