def playSound(frequency):
    buzzer.duty_u16(defaultLoudness)
    buzzer.freq(frequency)

def playSounds(frequency1, frequency2, frequency3):
    buzzer.duty_u16(defaultLoudness)
    secondaryBuzzer.duty_u16(defaultLoudness)
    tertiaryBuzzer.duty_u16(defaultLoudness)
    
    buzzer.freq(notes[frequency1])
    secondaryBuzzer.freq(notes[frequency2])
    if frequency3 != " " and frequency3 != "":
        tertiaryBuzzer.freq(notes[frequency3])

def stopSound():
    buzzer.duty_u16(0)
    secondaryBuzzer.duty_u16(0)
    tertiaryBuzzer.duty_u16(0)

