#Plays song
def playSong(song):
    showSongName(song[0])
    print(f"Now playing: {song[0]}")
    for i in range(2, len(song)):
        if (song[i] == " "):
            stopSound()
        else:
            #Plays 1 or more notes at once
            if len(song[i]) <= 3:
                playSound(notes[song[i]])
            else:
                note1 = note2 = note3 = " "
                
                #Separates first note from the rest
                if search("S", song[i]) and song[i].index("S") == 1:
                    note1 = song[i][0:3]
                    note2 = song[i][3::]
                else:
                    note1 = song[i][0:2]
                    note2 = song[i][2::]
                
                #Separates second note from third if third exists
                if search("S", note2) and note2.index("S") == 1:
                    note3 = note2[3::]
                    note2 = note2[0:3]
                else:
                    note3 = note2[2::]
                    note2 = note2[0:2]
                
                playSounds(note1, note2, note3)
        sleep(tempo)
    stopSound()

playSong(song)